

function overlayprocess() {
    // turnoffslideshow(),
    // $("div").removeClass("main-screen"),
    // animationstateaddition(),
    $(overlayContent).toggleClass("active"),
    setTimeout(function() {
        $(".overlay").toggleClass("active")
    }, 10)
}

function startexhibition() {
    setTimeout(function() {
        $(".start-screen").hide(),
        $(".stage").show()
    }, 400);
}

$(document).ready(function () {

    $(".start-btn").on("click", function () {
        startexhibition()
    }),
    $(".test-overlay").on("click", function () {
        overlayContent = ".overlay .about",
            overlayprocess()
    }),
    $("footer p").on("click", function () {
        overlayContent = ".overlay .author",
            overlayprocess()
    }),
    $(".overlay .close").on("click", function() {
        $(".overlay").toggleClass("active"),
        setTimeout(function() {
            $(".overlay div").removeClass("active")
        }, 500)
    })

})